INSERT INTO categories (id, parent_id, name, description) VALUES (1, null, 'Дорожная', 'Судя по названию несложно догадаться, что эти специальные машины нашли свое применение в строительстве и ремонте дорог. Их используют для укладки асфальтового покрытия и рельсового полотна. В связи с постоянным расширением дорожной сети, а также необходимостью регулярного ремонта уже существующих дорог, дорожная спецтехника пользуется повышенным спросом. Неудивительно, что ассортимент этих машин достаточно широк. Дорожная спецтехника применяется не только для непосредственной укладки внешнего покрытия, эта категория включает в себя также машины, выполняющие расчистку пространства для строящейся дороги, выравнивание участка, выкладку слоев основания (песок, гравий, сетка, смола и т.д.).');
INSERT INTO categories (id, parent_id, name, description) VALUES (2, null, 'Строительная', 'Строительная индустрия имеет невероятно много направлений. Если отделочные работы выполняются в основном вручную (с использованием специального инструмента), то капитальное строительство сложно себе представить без использования спецтехники.');
INSERT INTO categories (id, parent_id, name, description) VALUES (3, null, 'Сельскохозяйственная', 'Аграрная отрасль промышленности, имеет крайне важное значение для экономики страны. Сельское хозяйство обеспечивает сырьем и продуктами все без исключения производственные сферы. Основной транспорт, используемый в сельском хозяйстве трактор. Основные показатели, делающие эти машины незаменимыми для выполнения всех видов аграрных работ высокая мощность. Тракторы не отличаются высокими показателями маневренности и скорости, зато способны переносить большие нагрузки при использовании различного навесного оборудования они выполняют работы по подготовке почвы, посеву, сборке урожая, обслуживанию скота и т.д.');
INSERT INTO categories (id, parent_id, name, description) VALUES (4, null, 'Коммунальная или городская', 'К этому подвиду относятся специальные машины, предназначенные для выполнения ряда работ по обслуживанию городской инфраструктуры. Ассортимент коммунальной техники достаточно широк');
INSERT INTO categories (id, parent_id, name, description) VALUES (5, null, 'Производственная', 'Сюда относятся специальные машины и оборудование, предназначенные для обслуживания всех отраслей промышленности добывающей, лесной, обрабатывающей, машиностроительной и т.д.');
INSERT INTO categories (id, parent_id, name, description) VALUES (6, null, 'Транспортная', 'После того, как строительная площадка очищена и грунт подготовлен, необходимо доставить на место все необходимые материалы и инструменты. К этой категории относятся следующие машины');
INSERT INTO categories (id, parent_id, name, description) VALUES (7, null, 'Спецтехника для разработки месторождений', 'Судя по названию несложно догадаться что в эту категорию относятся машины и оборудование участвующие в процессе разработки месторождение полезных ископаемых');
INSERT INTO categories (id, parent_id, name, description) VALUES (8, null, 'Подъемная спецтехника', 'Данный вид машин выделяется в отдельную категорию, хотя по сути, подъемная спецтехника используется во многих отраслях промышленности (строительной, дорожной, при разработке месторождений). Несложно догадаться, что эти агрегаты используются для поднятия грузов на большую высоту');
INSERT INTO categories (id, parent_id, name, description) VALUES (9, null, 'Военная', 'Военная спецтехника, как таковая, в мирное время используется исключительно в процессе подготовки военнослужащих к участию к боевым действиям. Во время военных действий военные машины применяются для сдерживания агрессии и защиты мирного населения от неправомерных действий противника. Нередко военная спецтехника применяется для проведения операций по устранению гражданских угроз (задержание опасных вооруженных преступников, транспортировка случайно обнаруженных военных снарядов и т.д.)');
INSERT INTO categories (id, parent_id, name, description) VALUES (10, null, 'Особого назначения', 'К этой категории специальной техники относятся машины, используемые в процессе устранения любых чрезвычайных ситуаций');
INSERT INTO categories (id, parent_id, name, description) VALUES (11, null, 'Лесозаготовительная спецтехника', 'Данная категория включает в себя машины, выполняющие работы по заготовке леса, а также технику, применяемую для высаживание лесных территорий.');
INSERT INTO categories (id, parent_id, name, description) VALUES (12, null, 'Другое', '');
    INSERT INTO categories (id, parent_id, name, description) VALUES (14, 1, 'Грейдеры', 'Эти машины используются на начальных этапах строительства дорог. Их основная задача подготовить грунт, тщательно выровнять его и установить откосы.');
    INSERT INTO categories (id, parent_id, name, description) VALUES (15, 1, 'Асфальтоукладчики', 'Их основная функция работа с асфальтобетонной смесью на этапе её первоначальной укладки. Асфальтоукладчик вкладывает смесь на подготовленное полотно, тщательно выравнивают его и предварительно уплотняет.');
    INSERT INTO categories (id, parent_id, name, description) VALUES (16, 1, 'Катки', 'После того как асфальтоукладчик выложил и предварительно разровнял и уплотнилось асфальтобетонную смесь, приходит время работы катка. Основная функция этих специальных машин окончательное уплотнение и прессовка внешнего дорожного покрытия.');
    INSERT INTO categories (id, parent_id, name, description) VALUES (17, 1, 'Разметочные машины', 'Узкоспециализированные транспортные средства для нанесения разметки на дорожное полотно. В случае если дорожную разметку необходимо обновить или заменить, для удаления старых опознавательных знаков на дороге используются демаркировщики.');
    INSERT INTO categories (id, parent_id, name, description) VALUES (18, 1, 'Дорожные фрезы', 'Снимают старый слой асфальта во время проведения ремонтных работ.');
    INSERT INTO categories (id, parent_id, name, description) VALUES (19, 2, 'Экскаваторы', ' (основной рабочий элемент ковш) гусеничные и колесные, бульдозерные, цепные, роторные, карьерные, мини-экскаваторы, экскаваторы с функцией погрузчика');
    INSERT INTO categories (id, parent_id, name, description) VALUES (20, 2, 'Бульдозеры', '(основной рабочий элемент отвал) гусеничные и колесные, с поворотным неповоротным отвалом либо универсальные;');
    INSERT INTO categories (id, parent_id, name, description) VALUES (21, 2, 'Грейдеры', '(основной рабочий элемент отвал с ножом) гусеничные и колесные. Используются для снятия верхнего слоя грунта, а также для выравнивания площадки.');
    INSERT INTO categories (id, parent_id, name, description) VALUES (22, 2, 'Самосвалы', ' колёсные автомобили с большой грузоподъемностью. Основная особенность этих машин автоматический отбрасывающийся кузов, обеспечивающий максимально простую разгрузку, особенно если дело касается сыпучих материалов и строительных смесей.');
    INSERT INTO categories (id, parent_id, name, description) VALUES (23, 2, 'Погрузчики', 'На строительной площадке нередко приходится перевозить инструменты и материалы с места на место. Для того, чтобы максимально упростить процесс погрузки и разгрузки грузовых автомобилей, в строительстве используются погрузчики. Существует много модификаций этих машин со специальным навесным оборудованием, используемым в зависимости от типа груза (телескопические, вилочные), мини-погрузчики для работы на небольшом пространстве.Основная задача этого вида спецтехники доставка строительных материалов на большую высоту. Использование строительного крана целесообразно в процессе строительства высоких зданий либо объектов, значительно углубленных в землю. Краны в зависимости от способа передвижения бывают гусеничные и колесные. По комплектации и сфере использования автокраны делятся на мачтовые, телескопические, башенные, подъёмники, манипуляторы и т.д.');
    INSERT INTO categories (id, parent_id, name, description) VALUES (24, 2, 'Строительные Краны', 'Основная задача этого вида спецтехники доставка строительных материалов на большую высоту. Использование строительного крана целесообразно в процессе строительства высоких зданий либо объектов, значительно углубленных в землю. Краны в зависимости от способа передвижения бывают гусеничные и колесные. По комплектации и сфере использования автокраны делятся на мачтовые, телескопические, башенные, подъёмники, манипуляторы и т.д.');
    INSERT INTO categories (id, parent_id, name, description) VALUES (25, 2, 'Для подготовки строительных смесей', 'Самый известный пример такой машины бетономешалка. Эта спецтехника позволяет не просто смешивать цемент и другие сыпучие материалы в нужных пропорциях, но и обеспечивает доставку смеси непосредственно к месту использования.');
    INSERT INTO categories (id, parent_id, name, description) VALUES (26, 3, 'Сеялки', '');
    INSERT INTO categories (id, parent_id, name, description) VALUES (27, 3, 'Копалки', '');
    INSERT INTO categories (id, parent_id, name, description) VALUES (28, 3, 'Косилки', '');
    INSERT INTO categories (id, parent_id, name, description) VALUES (29, 3, 'Культиваторы', '');
    INSERT INTO categories (id, parent_id, name, description) VALUES (30, 3, 'Плуги', '');
    INSERT INTO categories (id, parent_id, name, description) VALUES (31, 3, 'Сепараторы', '');
    INSERT INTO categories (id, parent_id, name, description) VALUES (32, 4, 'Мусоровозы', 'Эти машины выполняют несколько функций: сбор мусора, его уплотнение и прессовка, транспортировка к месту утилизации. Современные мусоровозы оснащены объемными кузовами с задней загрузкой, хотя все еще можно увидеть более старые модели с ковшом, загружающим мусор через отверстие в верхней части кузова.');
    INSERT INTO categories (id, parent_id, name, description) VALUES (33, 4, 'Специальные машины для уборки дорог.', 'Они отлично справляются с очисткой дорожного покрытия от крупного и мелкого мусора, пыли, песка. Некоторые из них оснащены функцией полива дороги после очистки от загрязнений.');
    INSERT INTO categories (id, parent_id, name, description) VALUES (34, 4, 'Снегоуборочная техника', 'Эти агрегаты могут иметь совершенно разные габариты и мощность, в зависимости от климатических условий, в которых они используются. В северных регионах, характеризующихся затяжной зимой, снегоуборочная спецтехника работает минимум 5 месяцев году, расчищая снежные завалы и обеспечивая, тем самым, безопасность на дорогах. Крупные снегоуборочные машины используются для очистки проезжей части, мелкогабаритные агрегаты помогают убрать снег с тротуаров и пешеходных дорожек.');
    INSERT INTO categories (id, parent_id, name, description) VALUES (35, 4, 'Поливальные машины', 'Используются для полива клумб, газонов, парковых насаждений, а также для поддержания оптимальной температуры асфальтового покрытия дорожного полотна во время жары (при температуре выше 30 градусов асфальт теряет свои прочностные качества, становится пластичным и податливым, в результате чего при движении по нему тяжелых ТС он деформируется и со временем полностью разрушается).');
    INSERT INTO categories (id, parent_id, name, description) VALUES (36, 4, 'Автобашни', 'Машины, оборудованные телескопической стрелой с люлькой на конце. Используется для установки вывесок, ремонта уличного осветительного оборудования и т.д.');
INSERT INTO categories (id, parent_id, name, description) VALUES (37, 5, 'Погрузчики', '');
INSERT INTO categories (id, parent_id, name, description) VALUES (38, 5, 'Буровая спецтехника ', '');
INSERT INTO categories (id, parent_id, name, description) VALUES (39, 5, 'Спецтехника для разработки карьеров', '');
INSERT INTO categories (id, parent_id, name, description) VALUES (40, 5, 'Спецтехника для разработки месторождений полезных ископаемых', '');
