INSERT INTO categories (id, parent_id, name, description) VALUES (42, 6, 'Самосвалы', 'колёсные автомобили с большой грузоподъемностью. Основная особенность этих машин автоматический отбрасывающийся кузов, обеспечивающий максимально простую разгрузку, особенно если дело касается сыпучих материалов и строительных смесей');
INSERT INTO categories (id, parent_id, name, description) VALUES (43, 6, 'Погрузчики', 'На строительной площадке нередко приходится перевозить инструменты и материалы с места на место. Для того, чтобы максимально упростить процесс погрузки и разгрузки грузовых автомобилей, в строительстве используются погрузчики. Существует много модификаций этих машин со специальным навесным оборудованием, используемым в зависимости от типа груза (телескопические, вилочные), мини-погрузчики для работы на небольшом пространстве');
INSERT INTO categories (id, parent_id, name, description) VALUES (44, 6, 'Строительные краны', 'Основная задача этого вида спецтехники доставка строительных материалов на большую высоту. Использование строительного крана целесообразно в процессе строительства высоких зданий либо объектов, значительно углубленных в землю. Краны в зависимости от способа передвижения бывают гусеничные и колесные. По комплектации и сфере использования автокраны делятся на мачтовые, телескопические, башенные, подъёмники, манипуляторы и т.д.');
INSERT INTO categories (id, parent_id, name, description) VALUES (45, 6, 'Для подготовки строительных смесей', 'Самый известный пример такой машины бетономешалка. Эта спецтехника позволяет не просто смешивать цемент и другие сыпучие материалы в нужных пропорциях, но и обеспечивает доставку смеси непосредственно к месту использования.');
INSERT INTO categories (id, parent_id, name, description) VALUES (46, 7, 'Буровые машины', 'Колесные и гусеничные, буровые мачты');
INSERT INTO categories (id, parent_id, name, description) VALUES (47, 7, 'Для разработки карьеров', 'Например бульдозеры экскаваторы');
