INSERT INTO categories (id, parent_id, name, description) VALUES (63, 10, 'Медицинская спецтехника', '');
INSERT INTO categories (id, parent_id, name, description) VALUES (64, 10, 'Спецтехника МВД', '');
INSERT INTO categories (id, parent_id, name, description) VALUES (65, 11, 'Форвардеры', 'Специальные машины, выполняющие функцию погрузки, предварительной частичной сортировки и транспортировки леса.');
INSERT INTO categories (id, parent_id, name, description) VALUES (66, 11, 'Харвестеры', 'Машины, используемые для валки леса. Их использование в разы ускоряет процесс заготовки древесины.');
INSERT INTO categories (id, parent_id, name, description) VALUES (67, 11, 'Сажатели', 'Специальные транспортные средства, выполняющие основную работу в процессе высадки новых деревьев. С их помощью прокапываются траншеи, распределяются саженцы, выполняется процедура утрамбовки земли.');
INSERT INTO categories (id, parent_id, name, description) VALUES (68, 11, 'Измельчители', 'В процессе лесозаготовки, после окончания вырубки определенного участка, возникает необходимость убрать ветки и древесный мусор. С этой задачей быстро и эффективно справляются измельчители, которые перемалывают остатки стволов и ветки в мелкую стружку.');
INSERT INTO categories (id, parent_id, name, description) VALUES (69, 11, 'Лесовозы', 'Грузовики, оснащенные специально оборудованной платформой, предназначенные для транспортировки леса от места заготовки в точку переработки.');
INSERT INTO categories (id, parent_id, name, description) VALUES (70, 12, 'Исследовательская', 'Как бы не стремился человек познать все тайны нашей планеты, на сегодняшний день изучено лишь около 30% ее поверхности. Если говорить о суше, то здесь люди достигли значительных успехов, изучив ее на 98%. А вот участки Земли, покрытые водой, до сих пор являются для нас большой загадкой. И что уж говорить о космическом пространстве эта сфера для нас чистый лист, знания о котором достаточно обобщенные и размытые. В силу ограниченных возможностей человека в плане исследований территорий земли, воды и космоса, современными научными лабораториями регулярно разрабатываются специальные машины, способные выполнять эту сложную работу за людей. Исследовательская спецтехника может работать в неблагоприятных для человека условиях загрязненный воздух, плохая видимость, опасные участки. Прежде, чем отправляться исследовать дно океана, пещеру или горный разлом, ученые отправляют туда специальных дронов, которые предварительно оценивают уровень опасности, делают необходимые замеры, берут пробы. Именно благодаря исследовательской спецтехнике людьми были получены образцы почвы с поверхности луны. В качестве примеров исследовательской спецтехники можно назвать луноходы, исследовательские морские суда, специально оборудованные наземные комплексы.');
INSERT INTO categories (id, parent_id, name, description) VALUES (71, 12, 'Спецтехника правоохранительных служб', '')
