INSERT INTO techs_ordering.categories (id, parent_id, name, description) VALUES (10, 2, 'Экскаваторы', ' (основной рабочий элемент ковш) гусеничные и колесные, бульдозерные, цепные, роторные, карьерные, мини-экскаваторы, экскаваторы с функцией погрузчика');
INSERT INTO techs_ordering.categories (id, parent_id, name, description) VALUES (11, 2, 'Бульдозеры', '(основной рабочий элемент отвал) гусеничные и колесные, с поворотным неповоротным отвалом либо универсальные;');
INSERT INTO techs_ordering.categories (id, parent_id, name, description) VALUES (12, 2, 'Грейдеры', '(основной рабочий элемент отвал с ножом) гусеничные и колесные. Используются для снятия верхнего слоя грунта, а также для выравнивания площадки.');
INSERT INTO techs_ordering.categories (id, parent_id, name, description) VALUES (13, 2, 'Самосвалы', ' колёсные автомобили с большой грузоподъемностью. Основная особенность этих машин автоматический отбрасывающийся кузов, обеспечивающий максимально простую разгрузку, особенно если дело касается сыпучих материалов и строительных смесей.');
INSERT INTO techs_ordering.categories (id, parent_id, name, description) VALUES (14, 2, 'Погрузчики', 'На строительной площадке нередко приходится перевозить инструменты и материалы с места на место. Для того, чтобы максимально упростить процесс погрузки и разгрузки грузовых автомобилей, в строительстве используются погрузчики. Существует много модификаций этих машин со специальным навесным оборудованием, используемым в зависимости от типа груза (телескопические, вилочные), мини-погрузчики для работы на небольшом пространстве.Основная задача этого вида спецтехники доставка строительных материалов на большую высоту. Использование строительного крана целесообразно в процессе строительства высоких зданий либо объектов, значительно углубленных в землю. Краны в зависимости от способа передвижения бывают гусеничные и колесные. По комплектации и сфере использования автокраны делятся на мачтовые, телескопические, башенные, подъёмники, манипуляторы и т.д.');
INSERT INTO techs_ordering.categories (id, parent_id, name, description) VALUES (15, 2, 'Строительные Краны', 'Основная задача этого вида спецтехники доставка строительных материалов на большую высоту. Использование строительного крана целесообразно в процессе строительства высоких зданий либо объектов, значительно углубленных в землю. Краны в зависимости от способа передвижения бывают гусеничные и колесные. По комплектации и сфере использования автокраны делятся на мачтовые, телескопические, башенные, подъёмники, манипуляторы и т.д.');
INSERT INTO techs_ordering.categories (id, parent_id, name, description) VALUES (16, 2, 'Для подготовки строительных смесей', 'Самый известный пример такой машины бетономешалка. Эта спецтехника позволяет не просто смешивать цемент и другие сыпучие материалы в нужных пропорциях, но и обеспечивает доставку смеси непосредственно к месту использования.');
INSERT INTO techs_ordering.categories (id, parent_id, name, description) VALUES (17, 1, 'Грейдеры', 'Эти машины используются на начальных этапах строительства дорог. Их основная задача подготовить грунт, тщательно выровнять его и установить откосы.');
INSERT INTO techs_ordering.categories (id, parent_id, name, description) VALUES (18, 1, 'Асфальтоукладчики', 'Их основная функция работа с асфальтобетонной смесью на этапе её первоначальной укладки. Асфальтоукладчик вкладывает смесь на подготовленное полотно, тщательно выравнивают его и предварительно уплотняет.');
INSERT INTO techs_ordering.categories (id, parent_id, name, description) VALUES (19, 1, 'Катоки', 'После того как асфальтоукладчик выложил и предварительно разровнял и уплотнилось асфальтобетонную смесь, приходит время работы катка. Основная функция этих специальных машин окончательное уплотнение и прессовка внешнего дорожного покрытия.');
INSERT INTO techs_ordering.categories (id, parent_id, name, description) VALUES (20, 1, 'Разметочные машины', 'Узкоспециализированные транспортные средства для нанесения разметки на дорожное полотно. В случае если дорожную разметку необходимо обновить или заменить, для удаления старых опознавательных знаков на дороге используются демаркировщики.');
INSERT INTO techs_ordering.categories (id, parent_id, name, description) VALUES (21, 1, 'Дорожные фрезы', 'Снимают старый слой асфальта во время проведения ремонтных работ.');
