INSERT INTO tags (id, name, parent_id, description)
VALUES
        (1, 'Сфера применения', null, null),
            (2, 'Воздушная', 1, 'Сюда можно отнести грузовые самолёты и вертолёты, воздушную спецтехнику МЧС, вертолеты скорой помощи и т.д.'),
            (3, 'Водная', 1, 'Спецтехника, применяемая на воде. Это могут быть машины, используемые для строительства мостов, баржи, рыболовецкие судна, паромы.'),
            (4, 'Наземная', 1, 'Спецтехника, передвигающаяся по суше. Это колесные, гусеничные, рельсовые ТС.'),
            (5, 'Подводная', 1, 'Сюда относятся военные подлодки, исследовательские подводные суда, подводная строительная техника.'),
            (6, 'Космическая', 1, ' Такая спецтехника используется для исследования космоса ракеты и спутники, космические корабли, луноходы и т.д.'),
        (7, 'Тип машин', null, null),
            (8, 'Авто', 7, 'К автомобильной спецтехнике относятся машины и оборудование, передвигающиеся по поверхности земли: самосвалы, гусеничные тракторы, краны, лесовозы, снегоуборочные машины и пр.'),
            (9, 'Железнодорожная', 7, 'Это грузовые поезда и платформы, краны, передвигающиеся рельсовым способом.'),
            (10, 'Водная', 7, 'Сюда относят спецтехнику, работающую на воде или под водой'),
            (11, 'Воздушная', 7, 'Все виды воздушного спецтранспорта.'),
        (12, 'Способ передвижения', null, null),
            (13, 'Стационарные', 12, 'Все виды спецтехники, не имеющие специальных приспособлений для самостоятельного перемещения с места на место. Как правило, их транспортируют, используя другую специальную технику.'),
            (14, 'Колёсные', 12, 'В качестве приспособления для перемещения используются колёса. К этому подвиду относятся грузовики, самосвалы, колёсные тракторы, лесовозы и т.д.'),
            (15, 'Рельсовые', 12, 'Эти специальные машины могут передвигаться исключительно по рельсам.'),
            (16, 'Гусеничные', 12, 'Средства перемещения этих специальных транспортных средств гусеницы (гусеничные экскаваторы, военная гусеничная спецтехника, луноходы и т.д.).'),
            (17, 'Летательные', 12, 'Спецтехника перемещается по воздуху'),
        (18, 'Габариты', null, null),
            (19, 'Малогабаритные', 18, 'Чаще всего применяются в небольших хозяйствах. Компактные размеры, высокие показатели маневренности и проходимости позволяют этим машинам работать в ограниченном пространстве мини-тракторы, экскаваторы и самосвалы, компактные грузовики, погрузчики, небольшие снегоуборочные машины, малогабаритные спецсредства для строительства дорог (катки, разметочные автомобили) и т.д. Очевидное преимущество таких агрегатов экономичность и простота в обслуживании.'),
            (20, 'Среднегабпритные', 18, 'Техника специального назначения нашли свое применение в малом и среднем бизнесе. Эти машины имеют стандартные габариты, при этом показатели эффективности их гораздо выше, чем у мелких ТС. В качестве примера среднегабарита можно привести самосвал грузоподъемностью до 12 тонн, а также трактор мощностью до 150 л.с. с навесным оборудованием.'),
            (21, 'Крупногабаритные', 18, 'используются компаниями, выполняющими большой объем работ. На стройках промышленных масштабов используются мощные и производительные агрегаты, позволяющие максимально ускорить процесс возведения зданий и сооружений. Большие колхозы используют многофункциональные с/х комбайны, лесозаготовительные предприятия выполняют валку леса и его транспортировку с использованием специальных агрегатов повышенной грузоподъемности и мощности.'),
            (22, 'Негабаритные', 18, 'Эти машины характеризуются повышенной мощностью и грузоподъемностью, нестандартными показателями длины, ширины и высоты, большой массой. Такие ТС не всегда могут самостоятельно передвигаться по дорогам общего пользования (не проходят по габаритам, превышена максимально допустимая нагрузка на ось и пр.), поэтому доставляются к месту работы в разобранном виде на грузовых платформах. К таким ТС можно отнести большегрузные БелАЗы, используемые при разработке карьеров и месторождений полезных ископаемых (кстати, самый большой самосвал способен перевозить грузы, весом до 450 тонн).'),
        (23, 'Тип двигателя', null, 'Любая спецтехника относится к механическим ТС (приводится в движение двигателем). В настоящее время специальные машины могут быть оснащены различными видами двигателей:'),
            (24, 'Электромотор', 23, 'Двигатель берет энергию для работы из аккумулятора, который нужно периодически подзаряжать.'),
            (25, 'Дизельный двигатель', 23, 'Специальные ТС, работающие на дизтопливе составляют подавляющее большинство всех видов спецтехники. Практически все крупногабаритные машины и агрегаты работают на солярке.'),
            (26, 'Бензиновый мотор', 23, 'Т.к. использование бензина в промышленных масштабах не всегда экономически выгодно (бензин дороже дизельного топлива, его расход больше), бензиновая спецтехника используется чаще всего в мелких хозяйствах.'),
        (27, 'Шасси', null, 'В качестве средства передвижения наземной спецтехники могут использоваться несколько видов механизмов:'),
            (28, 'Колёса', 27, 'Колесные специальные ТС могут иметь различную колесную формулу (4х2, 4х4, 6х6, 8х4, 8х8 и т.д.). Как правило, они более маневренны, чем гусеничные (и тем более рельсовые). Кроме того, они свободно передвигаются по дорогам общего назначения.'),
            (29, 'Гусеницы', 27, 'Гусеничные ТС характеризуются повышенной проходимостью, они более мощные и устойчивые, могут работать в сложных условиях. Недостаток гусеничных ТС они не могут передвигаться по дорогам общего назначения, поэтому к месту работы их необходимо доставлять с помощью буксировочных машин.'),
            (30, 'Рельсовые шасси', 27, 'Такие ТС характеризуются повышенной устойчивостью и мощностью, поэтому используются при работе с тяжеловесными грузами. Двигаться они могут только по специально оборудованным рельсам, что значительно ограничивает их мобильность.'),
        (31, 'Марка производитель', null, 'На сегодняшний день недостатка в выборе спецтехники нет. Производством машин специального назначения занимаются во всех экономически развитых странах. Тем не менее, особой популярностью пользуются известные марки, отлично зарекомендовавшие себя на рынке:'),
            (32, 'Caterpillar', 31, 'Американский бренд, который на сегодняшний день считается абсолютным лидером по продажам спецтехники. Выпускает строительную, дорожную технику, самосвалы, электрические установки.'),
            (33, 'Komatsu', 31, 'Японский бренд, занимающий втрое место в мире по объемам выпуска машин и оборудования для промышленности. Выпускает строительную, погрузочную технику, ТС для разработки месторождений полезных ископаемых, военные машины.'),
            (34, 'Hitachi', 31, 'Крупная корпорация, одним из направлений деятельности которой является производство строительной техники (бульдозеры, самосвалы, погрузчики, краны и т.д.).'),
            (35, 'Volvo', 31, 'Шведский концерн, занимающийся производством автомобилей (легковых и грузовых), строительной техники, деталей для воздушного и водного транспорта.'),
            (36, 'Kubota', 31, 'Японская компания, специализирующаяся на выпуске тракторов, самосвалов, бульдозеров, трубопроводных кранов и т.д'),
            (37, 'Hiab', 31, 'Финский бренд, занимающийся выпусков кранов-манипуляторов различной конфигурации.'),
            (38, 'Liebherr', 31, 'Немецкая компания, выпускающая спецтехнику для промышленного и частного использования. Продукция самосвалы, краны, экскаваторы.'),
            (39, 'Terex', 31, 'Американский производитель строительной, добывающей, транспортной спецтехники.'),
        (40, 'Страна производитель', null, 'Если говорить о территориальном разделении производителей спецтехники, наиболее ярко выделяются в рейтинге следующие страны:'),
            (41, 'Япония', 40, 'Уверенно занимает первую позицию в рейтинге стран, занимающихся продажей и реализацией спецтехники. Еще год назад первенство принадлежало США, но сегодня Страна Восходящего Солнца отвоевала первенство 26% всей спецтехники, производится в Японии. Японское качество и здесь дает о себе знать отличное качество сборки, использование современных технологий, упрощающих управление и обслуживание машин и агрегатов, инновационные материалы. Из недостатков можно назвать лишь высокую стоимость этой продукции.'),
            (42, 'США', 40, 'Долгое время американская спецтехника занимала лидирующее место в мировом рейтинге. Продукция компаний США пользуется большим спросом по ряду объективных причин: высокое качество сборки, эргономичность, продуманный дизайн, большой ассортимент. На сегодняшний день на долю американских компаний приходится 24% всей производимой спецтехники.'),
            (43, 'Китай', 40, 'Замыкает тройку лидеров с показателем 16% от всего производимого в мире объема специальных ТС. Высокая конкуренция в этом сегменте отлично стимулирует китайских производителей выпускать качественную, надежную и долговечную спецтехнику. Плюс ко всему, радует цена, китайские машины на порядок дешевле японских или американских. Единственный минус многие покупатели с недоверием относятся к малоизвестным китайским брендам.'),
            (44, 'Южная Корея', 40, 'Доля в мировом объеме производства специальных ТС 5%. Азиатские производители, в том числе и Южная Корея, уверенно продвигаются в ТОП рейтинга высокое качество продукции, новейшие разработки в области управления делают эти машины востребованными во всем мире. Не может не радовать и весьма доступные цены корейские самосвалы, бульдозеры, краны гораздо дешевле японских, американских и европейских. Минус корейские бренды не так известны.'),
            (45, 'Швеция', 40, null),
            (46, 'Германия', 40, null),
            (47, 'Внликобритания', 40, null),
            (48, 'Франция', 40, null),
            (49, 'Италия', 40, null)
